mapper.update:
update goods
set
goods_number = #{num,jdbcType=SMALLINT}
where id = #{goodId,jdbcType=INTEGER}
and  is_on_sale = 1

update goods
set
goods_number = goods_number + #{num,jdbcType=SMALLINT}
where id = #{goodId,jdbcType=INTEGER}
and  is_on_sale = 1

mapper.select:
SELECT
g.id as goodsId,g.cat_id as goodsCatId,g.goods_sn as goodsGoodsSn,g.goods_name as goodsGoodsName,g.goods_number as goodsGoodsNumber,g.goods_weight as goodsGoodsWeight, g.weight_unit as goodsWeightUnit,g.shop_price as goodsShopPrice,g.goods_thumb as goodsGoodsThumb,g.goods_img as goodsGoodsImg,g.original_img as goodsOriginalImg, g.is_on_sale as goodsIsOnSale,g.is_shipping as goodsIsShipping,g.add_time as goodsAddTime,g.attr1 as goodsAttr1,g.attr2 as goodsAttr2,g.suppliers_id as goodsSuppliersId, g.ware_id as goodsWareId,g.trademark as goodsTrademark,g.remark as goodsRemark,
w.id as wareId,w.title as wareTitle,w.name as wareName,w.phone as warePhone,w.city as wareCity,w.address as wareAddress,w.content as wareContent, u.id as userId,u.username as userUsername, u.nickname as userNickname, attr.pingzhong as attrPingZhong,attr.attrname as attrAttrName,attr.material as attrMaterial,attr.specifications as attrSpecifications, st.id as storeinfoId,st.title as storeinfoTitle,st.user_id as storeinfoUserId,st.name as storeinfoName,st.phone as storeinfoPhone, st.business as storeinfoBusiness,st.city as storeinfoCity,st.address as storeinfoAddress,st.logo as storeinfoLogo,st.max_sellweight as storeMaxSellWeight, st.max_landweight as storeinfoMaxLandWeight,st.max_sellnum as storeinfoMaxSellNum,st.max_landnum as storeinfoMaxLandNum,st.created_at as storeinfoCreateAt, st.updated_at as storeinfoUpdatedAt,st.provinces as storeinfoProvinces,st.content as storeinfoContent
from goods as g left join (select a1.attr_name as pingzhong, a2.attr_name as attrname, a2.specifications as specifications, a2.material as material, a2.id as attrnameid from attribute a1 left join attribute a2 on a1.id = a2.parent_id WHERE a1.parent_id = 0) as attr on attr.attrnameid = g.cat_id left join users as u on g.suppliers_id = u.id left join storeinfo as st on st.user_id =u.id left join ware w on g.ware_id = w.id
g.is_on_sale = 1
and attr.pingzhong in
#{item, jdbcType=VARCHAR}
and attr.attrname in
#{item, jdbcType=VARCHAR}
and attr.material in
#{item, jdbcType=VARCHAR}
and attr.specifications in
#{item, jdbcType=VARCHAR}
and g.attr1 in
#{item, jdbcType=VARCHAR}
and g.attr2 in
#{item, jdbcType=VARCHAR}
and w.name = #{wareName,jdbcType=VARCHAR}
and g.shop_price >= #{priceLow,jdbcType=DECIMAL}
and g.shop_price <= #{priceHigh,jdbcType=DECIMAL}
order by
g.shop_price
g.shop_price desc
g.add_time desc
limit #{pageParam.startPage,jdbcType=INTEGER},#{pageParam.PageSize,jdbcType=INTEGER}

SELECT count(*) from goods as g left join (select a1.attr_name as pingzhong, a2.attr_name as attrname, a2.specifications as specifications, a2.material as material, a2.id as attrnameid from attribute a1 left join attribute a2 on a1.id = a2.parent_id WHERE a1.parent_id = 0) as attr on attr.attrnameid = g.cat_id left join users as u on g.suppliers_id = u.id left join storeinfo as st on st.user_id =u.id left join ware w on g.ware_id = w.id
g.is_on_sale = 1
and attr.pingzhong in
#{item, jdbcType=VARCHAR}
and attr.attrname in
#{item, jdbcType=VARCHAR}
and attr.material in
#{item, jdbcType=VARCHAR}
and attr.specifications in
#{item, jdbcType=VARCHAR}
and g.attr1 in
#{item, jdbcType=VARCHAR}
and g.attr2 in
#{item, jdbcType=VARCHAR}
and w.name = #{wareName,jdbcType=VARCHAR}
and g.shop_price >= #{priceLow,jdbcType=DECIMAL}
and g.shop_price <= #{priceHigh,jdbcType=DECIMAL}

SELECT
g.id as goodsId,g.cat_id as goodsCatId,g.goods_sn as goodsGoodsSn,g.goods_name as goodsGoodsName,g.goods_number as goodsGoodsNumber,g.goods_weight as goodsGoodsWeight, g.weight_unit as goodsWeightUnit,g.shop_price as goodsShopPrice,g.goods_thumb as goodsGoodsThumb,g.goods_img as goodsGoodsImg,g.original_img as goodsOriginalImg, g.is_on_sale as goodsIsOnSale,g.is_shipping as goodsIsShipping,g.add_time as goodsAddTime,g.attr1 as goodsAttr1,g.attr2 as goodsAttr2,g.suppliers_id as goodsSuppliersId, g.ware_id as goodsWareId,g.trademark as goodsTrademark,g.remark as goodsRemark,
w.id as wareId,w.title as wareTitle,w.name as wareName,w.phone as warePhone,w.city as wareCity,w.address as wareAddress,w.content as wareContent, u.id as userId,u.username as userUsername, u.nickname as userNickname, attr.pingzhong as attrPingZhong,attr.attrname as attrAttrName,attr.material as attrMaterial,attr.specifications as attrSpecifications, st.id as storeinfoId,st.title as storeinfoTitle,st.user_id as storeinfoUserId,st.name as storeinfoName,st.phone as storeinfoPhone, st.business as storeinfoBusiness,st.city as storeinfoCity,st.address as storeinfoAddress,st.logo as storeinfoLogo,st.max_sellweight as storeMaxSellWeight, st.max_landweight as storeinfoMaxLandWeight,st.max_sellnum as storeinfoMaxSellNum,st.max_landnum as storeinfoMaxLandNum,st.created_at as storeinfoCreateAt, st.updated_at as storeinfoUpdatedAt,st.provinces as storeinfoProvinces,st.content as storeinfoContent
from goods as g left join (select a1.attr_name as pingzhong, a2.attr_name as attrname, a2.specifications as specifications, a2.material as material, a2.id as attrnameid from attribute a1 left join attribute a2 on a1.id = a2.parent_id WHERE a1.parent_id = 0) as attr on attr.attrnameid = g.cat_id left join users as u on g.suppliers_id = u.id left join storeinfo as st on st.user_id =u.id left join ware w on g.ware_id = w.id
and attr.pingzhong = #{attrName, jdbcType=VARCHAR}
and attr.material = #{material, jdbcType=VARCHAR}
and attr.specifications = #{specifications, jdbcType=VARCHAR}
and g.attr1 = #{attr1, jdbcType=VARCHAR}
and g.is_on_sale = #{isOnSale, jdbcType=INTEGER}
and w.name = #{wareName,jdbcType=VARCHAR}
and u.id = #{supplierId,jdbcType=INTEGER}
order by g.add_time
limit #{pageParam.startPage,jdbcType=INTEGER},#{pageParam.PageSize,jdbcType=INTEGER}

