mapper.select:
select

from `position`

    c_id = #{c_id}

        and `name` = #{name,jdbcType=VARCHAR}



        and date(created_at) >= #{startTime,jdbcType=DATE}


        and date(created_at) <= #{endTime,jdbcType=DATE}



order by created_at asc

    limit #{pageParam.startPage,jdbcType=INTEGER},#{pageParam.PageSize,jdbcType=INTEGER}

 select
count(*)
 from `position`

     c_id = #{c_id}

         and `name` = #{name,jdbcType=VARCHAR}



         and date(created_at) >= #{startTime,jdbcType=DATE}


         and date(created_at) <= #{endTime,jdbcType=DATE}
